import React, { useState, useEffect } from "react";
import html2canvas from "html2canvas";

import MinigameOverlay from "./MinigameOverlay"; // 미니게임 오버레이 가져오기

export default function FriendOcean() {
  const [bgIndex, setBgIndex] = useState(0);
  const [showGame, setShowGame] = useState(false);
  const [placedItems, setPlacedItems] = useState([]);
  const [inventory, setInventory] = useState(["Shell", "Treasure"]); // 더미 아이템 예시

  const moveLeft = () => { if (bgIndex > 0) setBgIndex(bgIndex - 1); };
  const moveRight = () => { if (bgIndex < 3) setBgIndex(bgIndex + 1); };

  // 화면 캡처
  const captureScreen = () => {
    const appElement = document.querySelector(".app");
    if (!appElement) return;
    html2canvas(appElement).then(canvas => {
      const link = document.createElement("a");
      link.href = canvas.toDataURL("image/png");
      link.download = "my_ocean_capture.png";
      link.click();
    });
  };

  // 아이템 배치
  const handlePlace = (item) => {
    setPlacedItems(prev => [...prev, item]);
  };

  // 미니게임에서 아이템 보상 받기
  const handleGameReward = (newItem) => {
    setInventory(prev => [...prev, newItem]);
  };

  return (
    <div className="app">
      <div
        className="background-wrap"
        style={{ transform: `translateX(-${bgIndex * 25}%)` }}
      >
        {[...Array(4)].map((_, i) => (
          <div
            key={i}
            className="background"
            style={{ backgroundImage: "url('/background.png')" }}
          />
        ))}
      </div>

      <div className="fish-container">
        {placedItems.map((item, i) => (
          <div
            key={i}
            style={{
              position: "absolute",
              top: `${20 + i * 10}%`,
              left: `${15 + i * 8}%`
            }}
          >
            🐚
          </div>
        ))}
      </div>

      <div className="left-menu">
        <button
          className="menu-btn"
          onClick={() => alert("도움말/설정 모달 등")}
        >
          📜
        </button>
        <button
          className="menu-btn"
          onClick={() => setShowGame(true)} // 미니게임 오픈
        >
          🎮
        </button>
      </div>

      <button className="arrow arrow-left" onClick={moveLeft}>⬅</button>
      <button className="arrow arrow-right" onClick={moveRight}>➡</button>

      <div className="bottom-btns">
        <button className="circle-btn" onClick={captureScreen}>📷</button>
      </div>

      {/* 아이템 목록 */}
      <div
        style={{
          position: "absolute",
          bottom: "80px",
          left: "20px",
          background: "#fff",
          padding: "10px",
          borderRadius: "10px"
        }}
      >
        <h3 style={{ margin: "0 0 5px 0" }}>보관함</h3>
        {inventory.length === 0 ? (
          <p>아이템 없음</p>
        ) : (
          <ul style={{ listStyle: "none", padding: 0 }}>
            {inventory.map((item, idx) => (
              <li key={idx} style={{ marginTop: "5px" }}>
                {item}
                <button
                  onClick={() => handlePlace(item)}
                  style={{ marginLeft: "10px" }}
                >
                  배치
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* 미니게임 오버레이 */}
      {showGame && (
        <MinigameOverlay
          onClose={() => setShowGame(false)}
          onReward={handleGameReward}
        />
      )}
    </div>
  );
}
