import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import BitFish from './/components/BitFish';
import Login from './components/Login';

 import Signup from './components/Signup';
 import Aquarium from './components/Aquarium';
 import MiniGame from './components/MiniGame';
 import Archive from './components/Archive';
 import OceanView from './pages/OceanView';
 import DecorateView from './pages/DecorateView';
 import FriendOcean from './pages/FriendOcean';
 import KakaoCallback from './components/Auth/KakaoCallback';
 import FishPosts from './components/FishPosts'; // 필요시

import './App.css';

export default function App() {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const navigate = useNavigate();

  useEffect(() => {
    if (token && !user) {
      fetch('/api/auth/me', {
        headers: { 'Authorization': `Bearer ${token}` }
      })
        .then(res => (res.ok ? res.json() : Promise.reject(res.status)))
        .then(data => setUser(data))
        .catch(err => {
          console.error('Failed to fetch user info:', err);
          localStorage.removeItem('token');
          setToken('');
          navigate('/login');
        });
    }
  }, [token, user, navigate]);

  const handleLoginSuccess = (newToken, newUser) => {
    localStorage.setItem('token', newToken);
    setToken(newToken);
    setUser(newUser);
  };

  const RequireAuth = ({ children }) => {
    return token ? children : <Navigate to="/login" replace />;
  };

  return (
    <div className="app">
      <Routes>
        {/* 기본 경로는 BitFish 웰컴페이지로 리다이렉트 */}
        <Route path="/" element={<Navigate to="/bitfish" replace />} />
        <Route path="/bitfish" element={<BitFish />} />
        <Route path="/login" element={<Login onLogin={handleLoginSuccess} />} />
        {/* 나머지 라우트는 필요에 따라 추가하세요. */}
      </Routes>
    </div>
  );
}
