import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Login.css"; // 아래 CSS를 별도 파일로 분리해도 됨

export default function Login() {
  const navigate = useNavigate();
  const [isMemberLogin, setIsMemberLogin] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // 카카오톡 로그인 예시
  const handleKakaoLogin = () => {
    alert("카카오톡 로그인 처리 (추가 구현 필요)");
    // 로그인 후 이동:
    // navigate("/my");
  };

  // 회원 로그인 예시
  const handleMemberLogin = () => {
    alert(`이메일: ${email}, 비밀번호: ${password}`);
    // fetch("/api/auth/login", { ... });
    // navigate("/my");
  };

  return (
    <div className="login-page">
      {/* 바다 배경을 깔아둔 뒤 가운데 카드 배치 */}
      <div className="login-card">
        {isMemberLogin ? (
          <>
            <h2>로그인</h2>
            <input
              type="text"
              placeholder="이메일을 입력하세요..."
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
            <input
              type="password"
              placeholder="비밀번호를 입력하세요..."
              value={password}
              onChange={e => setPassword(e.target.value)}
            />
            <button className="btn-confirm" onClick={handleMemberLogin}>
              확인
            </button>
            <button className="btn-back" onClick={() => setIsMemberLogin(false)}>
              뒤로
            </button>
          </>
        ) : (
          <>
            <button className="login-btn" onClick={handleKakaoLogin}>
              카카오톡으로 로그인
            </button>
            <button className="login-btn" onClick={() => setIsMemberLogin(true)}>
              회원으로 로그인
            </button>
            <div className="signup-link">
              회원이 아니신가요?{" "}
              <Link to="/signup" style={{ textDecoration: "underline" }}>
                회원가입
              </Link>
            </div>
          </>
        )}
      </div>
    </div>
  );
}
