import type React from "react"

import { useState, useEffect } from "react"
import { Fish, Plus } from "lucide-react"
import { motion } from "framer-motion"
import { Card, CardContent } from "./ui/card"
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
  DialogClose,
} from "./ui/dialog"
import { Button } from "./ui/button"
import { Textarea } from "./ui/textarea"
import { Input } from "./ui/input"
import { Label } from "./ui/label"

// Type for our posts

// Available fish colors
const FISH_COLORS = [
  "#3b82f6", // blue
  "#ef4444", // red
  "#10b981", // green
  "#f59e0b", // amber
  "#8b5cf6", // purple
  "#ec4899", // pink
]

export default function FishPosts() {
  const [posts, setPosts] = useState<[]>([])
  const [openPostId, setOpenPostId] = useState<string | null>(null)
  const [isAddingPost, setIsAddingPost] = useState(false)
  const [newPost, setNewPost] = useState({
    author: "",
    title: "",
    content: "",
  })

  // Load posts from localStorage on component mount
  useEffect(() => {
    const savedPosts = localStorage.getItem("fishPosts")
    if (savedPosts) {
      setPosts(JSON.parse(savedPosts))
    }
  }, [])

  // Save posts to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem("fishPosts", JSON.stringify(posts))
  }, [posts])

  const handleAddPost = () => {
    if (!newPost.author || !newPost.title || !newPost.content) {
      return // Don't add empty posts
    }

    const post: Post = {
      id: Date.now().toString(),
      author: newPost.author,
      title: newPost.title,
      content: newPost.content,
      color: FISH_COLORS[Math.floor(Math.random() * FISH_COLORS.length)],
      timestamp: Date.now(),
    }

    setPosts([...posts, post])
    setNewPost({ author: "", title: "", content: "" })
    setIsAddingPost(false)
  }

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target
    setNewPost((prev) => ({ ...prev, [name]: value }))
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-sky-100 to-sky-400 p-6">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold text-center mb-8 text-sky-900">친구 바다 꾸미기</h1>

        {posts.length === 0 ? (
          <div className="text-center text-sky-800 mb-12">
            <p className="mb-4">아직 바다에 물고기가 없어요! 첫 번째 메시지를 작성해보세요.</p>
          </div>
        ) : (
          <p className="text-center text-sky-800 mb-12">물고기를 클릭하면 친구들이 작성한 메시지를 볼 수 있어요!</p>
        )}

        <div className="relative h-[500px] bg-gradient-to-b from-sky-300 to-sky-600 rounded-xl overflow-hidden shadow-xl border border-sky-200">
          {/* Decorative elements */}
          <div className="absolute bottom-0 left-0 right-0 h-20 bg-amber-200 opacity-70"></div> {/* Sand */}
          <div className="absolute top-4 right-8">
            <div className="w-16 h-16 rounded-full bg-yellow-300 opacity-80"></div> {/* Sun */}
          </div>
          {/* Add post button */}
          <div className="absolute bottom-6 right-6 z-10">
            <Dialog open={isAddingPost} onOpenChange={setIsAddingPost}>
              <DialogTrigger asChild>
                <Button size="icon" className="h-14 w-14 rounded-full bg-sky-600 hover:bg-sky-700 shadow-lg">
                  <Plus className="h-6 w-6" />
                  <span className="sr-only">메시지 작성하기</span>
                </Button>
              </DialogTrigger>
              <DialogContent className="sm:max-w-md">
                <DialogHeader>
                  <DialogTitle>새 메시지 작성하기</DialogTitle>
                </DialogHeader>
                <div className="grid gap-4 py-4">
                  <div className="grid gap-2">
                    <Label htmlFor="author">작성자</Label>
                    <Input
                      id="author"
                      name="author"
                      placeholder="이름을 입력하세요"
                      value={newPost.author}
                      onChange={handleInputChange}
                    />
                  </div>
                  <div className="grid gap-2">
                    <Label htmlFor="title">제목</Label>
                    <Input
                      id="title"
                      name="title"
                      placeholder="제목을 입력하세요"
                      value={newPost.title}
                      onChange={handleInputChange}
                    />
                  </div>
                  <div className="grid gap-2">
                    <Label htmlFor="content">내용</Label>
                    <Textarea
                      id="content"
                      name="content"
                      placeholder="바다에서의 추억이나 이야기를 적어보세요..."
                      rows={5}
                      value={newPost.content}
                      onChange={handleInputChange}
                    />
                  </div>
                </div>
                <DialogFooter>
                  <DialogClose asChild>
                    <Button variant="outline">취소</Button>
                  </DialogClose>
                  <Button onClick={handleAddPost}>물고기 만들기</Button>
                </DialogFooter>
              </DialogContent>
            </Dialog>
          </div>
          {/* Fish posts */}
          {posts.map((post, index) => (
            <Dialog
              key={post.id}
              open={openPostId === post.id}
              onOpenChange={(open) => setOpenPostId(open ? post.id : null)}
            >
              <DialogTrigger asChild>
                <motion.div
                  className="absolute cursor-pointer"
                  style={{
                    top: `${100 + (index % 5) * 70}px`,
                    left: `${index % 2 === 0 ? 20 : 70}%`,
                  }}
                  animate={{
                    x: [0, index % 2 === 0 ? 100 : -100, 0],
                    y: [0, -20, 0],
                  }}
                  transition={{
                    duration: 5 + (index % 5),
                    repeat: Number.POSITIVE_INFINITY,
                    ease: "easeInOut",
                  }}
                >
                  <div className="relative group">
                    <Fish size={60 + (index % 3) * 10} color={post.color} fill={post.color} strokeWidth={1} />
                    <div className="absolute -top-8 left-1/2 transform -translate-x-1/2 bg-white px-2 py-1 rounded text-xs opacity-0 group-hover:opacity-100 transition-opacity whitespace-nowrap">
                      {post.author}의 이야기
                    </div>
                  </div>
                </motion.div>
              </DialogTrigger>
              <DialogContent className="sm:max-w-md">
                <DialogHeader>
                  <DialogTitle>{post.title}</DialogTitle>
                </DialogHeader>
                <div className="flex flex-col gap-4">
                  <div className="text-sm text-muted-foreground">작성자: {post.author}</div>
                  <Card>
                    <CardContent className="pt-6">{post.content}</CardContent>
                  </Card>
                  <div className="text-xs text-muted-foreground text-right">
                    {new Date(post.timestamp).toLocaleString("ko-KR")}
                  </div>
                </div>
              </DialogContent>
            </Dialog>
          ))}
          {/* Bubbles animation */}
          {[...Array(10)].map((_, i) => (
            <motion.div
              key={i}
              className="absolute rounded-full bg-white/30"
              style={{
                width: `${Math.random() * 20 + 5}px`,
                height: `${Math.random() * 20 + 5}px`,
                left: `${Math.random() * 100}%`,
                bottom: `-20px`,
              }}
              animate={{
                y: [0, -500],
                opacity: [0.7, 0],
              }}
              transition={{
                duration: Math.random() * 10 + 5,
                repeat: Number.POSITIVE_INFINITY,
                delay: Math.random() * 5,
              }}
            />
          ))}
        </div>

        <div className="mt-8 text-center text-sky-800">
          {posts.length === 0 ? (
            <p>오른쪽 하단의 + 버튼을 눌러 첫 번째 메시지를 작성해보세요!</p>
          ) : (
            <p>물고기 위에 마우스를 올리면 작성자를 볼 수 있고, 클릭하면 메시지를 읽을 수 있어요!</p>
          )}
        </div>
      </div>
    </div>
  )
}
