// src/pages/DecorateView.jsx
import React, { useState, useEffect } from "react";
import html2canvas from "html2canvas";
import { useParams, useNavigate } from "react-router-dom";
import "../App.css";

export default function DecorateView() {
  const { username } = useParams();
  const navigate = useNavigate();
  const [bgIndex, setBgIndex] = useState(0);
  const [musicPlaying, setMusicPlaying] = useState(false);
  const [fishPaused, setFishPaused] = useState(false);
  const [modal, setModal] = useState("");
  const [inventory, setInventory] = useState([]);
  const [placedItems, setPlacedItems] = useState([]);

  useEffect(() => {
    // ÏπúÍµ¨Ïùò Î≥¥Í¥ÄÌï® Î∂àÎü¨Ïò§Í∏∞
    fetch(`/api/items/${username}`)
      .then(res => res.json())
      .then(data => {
        if (data && Array.isArray(data.inventory)) {
          setInventory(data.inventory);
        }
      })
      .catch(err => console.error("ÏïÑÏù¥ÌÖú Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®", err));
  }, [username]);

  const moveLeft = () => {
    if (bgIndex > 0) setBgIndex(bgIndex - 1);
  };
  const moveRight = () => {
    if (bgIndex < 3) setBgIndex(bgIndex + 1);
  };

  const captureScreen = () => {
    const appElement = document.querySelector(".app");
    html2canvas(appElement).then((canvas) => {
      const link = document.createElement("a");
      link.href = canvas.toDataURL("image/png");
      link.download = "my_ocean_capture.png";
      link.click();
    });
    const sound = document.getElementById("captureSound");
    if (sound) sound.play();
  };

  const copyUrl = () => {
    navigator.clipboard.writeText(window.location.href)
      .then(() => alert("URLÏù¥ Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§!"));
  };

  // ÏïÑÏù¥ÌÖú Î∞∞Ïπò
  const handlePlace = (itemId) => {
    // ÏïÑÏù¥ÌÖúÏùÑ Î∞∞Ïπò Î™©Î°ùÏóê Ï∂îÍ∞Ä
    setPlacedItems(prev => [...prev, itemId]);
  };

  // Î∞∞ÏπòÎêú ÏïÑÏù¥ÌÖú ÏÑúÎ≤ÑÏóê Ï†ÄÏû•
  const handleSubmit = () => {
    fetch(`/api/ocean/${username}`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ items: placedItems })
    })
      .then(res => {
        if (res.ok) {
          alert("Ïñ¥Ìï≠ÏùÑ Íæ∏Î©∞Ï§¨Ïñ¥Ïöî!");
          navigate(`/ocean/${username}`);
        } else {
          alert("Ï†ÄÏû• Ïã§Ìå® „Ö†„Ö†");
        }
      });
  };

  return (
    <div className="app">
      {/* Î∞∞Í≤Ω 4Í∞ú Ïä¨ÎùºÏù¥Îìú */}
      <div className="background-wrap" style={{ transform: `translateX(-${bgIndex * 25}%)` }}>
        {[...Array(4)].map((_, i) => (
          <div
            key={i}
            className="background"
            style={{ backgroundImage: "url('/background.png')" }}
          ></div>
        ))}
      </div>

      {/* Ïñ¥Ìï≠ Î†àÏù¥Ïñ¥(Î¨ºÍ≥†Í∏∞, Î∞∞Ïπò ÏïÑÏù¥ÌÖú Îì±) */}
      <div
        className="fish-container"
        style={{ animationPlayState: fishPaused ? "paused" : "running" }}
      >
        {placedItems.map((itemId, i) => (
          <div
            key={i}
            className="placed-item"
            style={{
              position: "absolute",
              top: `${20 + i * 10}%`,
              left: `${20 + i * 10}%`
            }}
          >
            üêö
          </div>
        ))}
      </div>

      {/* Ï¢åÏ∏° Î©îÎâ¥ */}
      <div className="left-menu">
        <button className="menu-btn" onClick={() => setModal("help")}>üìú</button>
        <button className="menu-btn" onClick={() => setModal("setting")}>‚öôÔ∏è</button>
        <button className="menu-btn" onClick={() => setModal("copy")}>üîó</button>
        <button className="menu-btn" onClick={() => navigate("/")}>‚¨ÖÔ∏è</button>
      </div>

      {/* Î∞∞Í≤Ω Ïù¥Îèô Î≤ÑÌäº */}
      <button className="arrow arrow-left" onClick={moveLeft}>‚¨Ö</button>
      <button className="arrow arrow-right" onClick={moveRight}>‚û°</button>

      {/* ÌïòÎã® Ï∫°Ï≤ò Î≤ÑÌäº */}
      <div className="bottom-btns">
        <button className="circle-btn" onClick={captureScreen}>üì∑</button>
      </div>

      {/* Î™®Îã¨ */}
      {modal && (
        <div className="modal" onClick={() => setModal("")}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            {modal === "help" && (
              <>
                <div className="cute-icon">üòä</div>
                <h2>ÎèÑÏõÄÎßê</h2>
                <ul style={{ textAlign: "left", paddingLeft: "20px" }}>
                  <li>ÏïÑÏù¥ÌÖúÏùÑ ÏÑ†ÌÉùÌï¥ÏÑú ÏπúÍµ¨ Ïñ¥Ìï≠Ïóê Î∞∞ÏπòÌï† Ïàò ÏûàÏñ¥Ïöî.</li>
                  <li>Í≤åÏûÑÏùÑ ÌÜµÌï¥ ÏñªÏùÄ ÏïÑÏù¥ÌÖúÏù¥ Ïó¨Í∏∞Ïóê Î≥¥Ïó¨Ïöî.</li>
                </ul>
              </>
            )}
            {modal === "setting" && (
              <>
                <div className="cute-icon">üéõÔ∏è</div>
                <h2>ÏÑ§Ï†ï</h2>
                <div className="setting-options">
                  <div
                    className="setting-option"
                    onClick={() => {
                      setMusicPlaying(!musicPlaying);
                      alert(musicPlaying ? "ÏùåÏïÖÏù¥ Ï†ïÏßÄÎê©ÎãàÎã§." : "ÏùåÏïÖÏù¥ Ïû¨ÏÉùÎê©ÎãàÎã§.");
                    }}
                  >
                    üîá
                  </div>
                  <div
                    className="setting-option"
                    onClick={() => {
                      setFishPaused(!fishPaused);
                      alert(fishPaused ? "Î¨ºÍ≥†Í∏∞ Ïï†ÎãàÎ©îÏù¥ÏÖòÏù¥ Ï†ïÏßÄÎêòÏóàÏäµÎãàÎã§." : "Î¨ºÍ≥†Í∏∞ Ïï†ÎãàÎ©îÏù¥ÏÖòÏù¥ Ïû¨ÏÉùÎê©ÎãàÎã§.");
                    }}
                  >
                    ‚è∏Ô∏è
                  </div>
                </div>
              </>
            )}
            {modal === "copy" && (
              <>
                <div className="cute-icon">üîó</div>
                <h2>URL Î≥µÏÇ¨</h2>
                <div className="url-box">
                  <span>{window.location.href}</span>
                  <button onClick={copyUrl}>Î≥µÏÇ¨</button>
                </div>
              </>
            )}
            <button onClick={() => setModal("")}>Îã´Í∏∞</button>
          </div>
        </div>
      )}

      {/* üî• Í≤åÏûÑÌï¥ÏÑú ÏïÑÏù¥ÌÖú ÏñªÍ∏∞ Î≤ÑÌäº (ÏÉÅÎã® Ïö∞Ï∏°) */}
      <div
        style={{
          position: "absolute",
          top: "10px",
          right: "10px"
        }}
      >
