import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import Aquarium from './components/Aquarium';
import Login from './components/Login';
import Signup from './components/Signup';
import MiniGame from './components/MiniGame';
import Archive from './components/Archive';
import OceanView from './pages/OceanView';
import DecorateView from './pages/DecorateView';
import FriendOcean from './pages/FriendOcean';

export default function App() {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const navigate = useNavigate();

  useEffect(() => {
    if (token && !user) {
      fetch('/api/auth/me', {
        headers: { 'Authorization': `Bearer ${token}` }
      })
        .then(res => res.ok ? res.json() : Promise.reject(res.status))
        .then(data => setUser(data))
        .catch(err => {
          console.error('Failed to fetch user info:', err);
          localStorage.removeItem('token');
          setToken('');
          navigate('/login');
        });
    }
  }, [token]);

  const handleLoginSuccess = (newToken, newUser) => {
    localStorage.setItem('token', newToken);
    setToken(newToken);
    setUser(newUser);
  };

  const RequireAuth = ({ children }) => {
    return token ? children : <Navigate to="/login" replace />;
  };

  return (
    <Routes>

      {/* 루트: 토큰이 있으면 /aquarium, 없으면 /login */}
      <Route
        path="/"
        element={
          token
            ? <Navigate to="/aquarium" replace />
            : <Navigate to="/login" replace />
        }
      />

      {/* 로그인, 회원가입 */}
      <Route
        path="/login"
        element={<Login onLogin={handleLoginSuccess} />}
      />
      <Route
        path="/signup"
        element={<Signup onSignup={handleLoginSuccess} />}
      />

      {/* 친구 어항 (로그인 필요 여부에 따라 수정 가능) */}
      <Route path="/friend" element={<FriendOcean />} />

      {/* 어항, 미니게임, 보관함 등 (RequireAuth 필요) */}
      <Route
        path="/aquarium"
        element={
          <RequireAuth>
            <Aquarium user={user} token={token} />
          </RequireAuth>
        }
      />
      <Route
        path="/minigame"
        element={
          <RequireAuth>
            <MiniGame token={token} />
          </RequireAuth>
        }
      />
      <Route
        path="/archive"
        element={
          <RequireAuth>
            <Archive token={token} />
          </RequireAuth>
        }
      />

      {/* 누구나 접근 가능: /ocean/:username, /decorate/:username */}
      <Route path="/ocean/:username" element={<OceanView />} />
      <Route path="/decorate/:username" element={<DecorateView />} />

    </Routes>
  );
}
