import React, { useEffect } from 'react';

function KakaoLogin() {
  useEffect(() => {
    // Kakao SDK 초기화
    if (window.Kakao && !window.Kakao.isInitialized()) {
      window.Kakao.init('fa003cbb07c8656757bdf385384e8987'); // 실제 앱 키로 교체 가능
    }
  }, []);

  const loginWithKakao = () => {
    if (window.Kakao) {
      window.Kakao.Auth.authorize({
        redirectUri: 'http://localhost:3000/oauth/kakao/callback',
      });
    } else {
      alert('카카오 SDK 로드 실패');
    }
  };

  return (
    <button
      onClick={loginWithKakao}
      style={{
        backgroundColor: '#FEE500',
        color: '#000',
        padding: '10px 20px',
        borderRadius: '8px',
        fontWeight: 'bold',
        border: 'none',
        cursor: 'pointer',
        marginTop: '1rem',
      }}
    >
      카카오톡으로 로그인
    </button>
  );
}

export default KakaoLogin;
