import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import html2canvas from "html2canvas";

const cssString = `
@import url('https://fonts.googleapis.com/css2?family=Jua&family=Dongle:wght@400;700&family=Gowun+Dodum&display=swap');

html, body {
  margin: 0;
  padding: 0;
  width: 100vw;
  height: 100vh;
  background: #000;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
  font-family: 'Jua','Dongle','Gowun Dodum', sans-serif;
}

.app {
  position: relative;
  width: 100vw;
  max-width: 414px;
  aspect-ratio: 9 / 16;
  background: #eee;
  overflow: hidden;
  border-radius: 10px;
  box-shadow: 0 0 10px rgba(0,0,0,0.5);
}

.background-wrap {
  position: absolute;
  top: 0; left: 0;
  width: 400%;
  height: 100%;
  display: flex;
  transition: transform 0.6s ease;
}

.background {
  width: 25%;
  height: 100%;
  background-repeat: no-repeat;
  background-size: cover;
  background-position: center;
}

.left-menu {
  position: absolute;
  top: 10%;
  left: 10px;
  display: flex;
  flex-direction: column;
  gap: 8px;
  z-index: 10;
}

.menu-btn {
  width: 40px;
  height: 40px;
  background: rgba(0,0,0,0.5);
  color: #fff;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  font-size: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
}

.arrow {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  width: 45px;
  height: 45px;
  background: rgba(0,0,0,0.5);
  color: #fff;
  border: none;
  border-radius: 50%;
  font-size: 18px;
  cursor: pointer;
  z-index: 10;
}
.arrow-left {
  left: 15px;
}
