function useInjectStyle(text) {
  useEffect(() => {
    const styleEl = document.createElement("style");
    styleEl.innerHTML = text;
    document.head.appendChild(styleEl);
    return () => {
      document.head.removeChild(styleEl);
    };
  }, [text]);
}

function RpsGame({ onClose, onReward }) {
  const [score, setScore] = useState(0);
  const [timeLeft, setTimeLeft] = useState(10);
  const [gameActive, setGameActive] = useState(false);

  useEffect(() => {
    let timer;
    if (gameActive && timeLeft > 0) {
      timer = setInterval(() => setTimeLeft(t => t - 1), 1000);
    }
    if (gameActive && timeLeft === 0) {
      endGame();
    }
    return () => clearInterval(timer);
  }, [gameActive, timeLeft]);

  function startGame() {
    setScore(0);
    setTimeLeft(10);
    setGameActive(true);
  }

  function endGame() {
    setGameActive(false);
    if (score >= 3) {
      onReward("NewItem");
      alert("아이템 획득!");
    } else {
      alert("아쉽네요. 다음에 다시 시도!");
    }
    onClose();
  }

  function clickCircle() {
    if (gameActive) setScore(s => s + 1);
  }

  return (
    <div
      style={{
        position: "fixed",
        top: 0, left: 0,
        width: "100vw", height: "100vh",
        background: "rgba(0,0,0,0.5)",
        zIndex: 999,
        display: "flex",
        alignItems: "center",
        justifyContent: "center"
      }}
    >
      {!gameActive && (
        <div
          style={{
            background: "#fff",
            padding: "20px",
            borderRadius: "8px",
            textAlign: "center"
          }}
        >
          <h2>미니게임</h2>
          <button onClick={startGame} style={{ marginRight: "10px" }}>시작</button>
          <button onClick={onClose}>닫기</button>
        </div>
      )}
      {gameActive && (
        <>
          <div
            style={{
              background: "#fff",
              padding: "10px 20px",
              borderRadius: "8px",
              position: "absolute",
              top: "20px"
            }}
          >
            <div>남은 시간: {timeLeft}s</div>
            <div>점수: {score}</div>
          </div>
          <div
            onClick={clickCircle}
            style={{
              width: "50px",
              height: "50px",
              borderRadius: "50%",
              background: "red",
              position: "absolute",
              top: `${Math.random() * 60 + 20}vh`,
              left: `${Math.random() * 60 + 20}vw`,
              cursor: "pointer"
            }}
          />
        </>
      )}
    </div>
  );
}

export default function FriendOceanOneFile() {
  useInjectStyle(cssString);

  const navigate = useNavigate();
  const [bgIndex, setBgIndex] = useState(0);
  const [modal, setModal] = useState("");
  const [musicPlaying, setMusicPlaying] = useState(false);
  const [fishPaused, setFishPaused] = useState(false);
  const [inventory, setInventory] = useState(["Shell", "Treasure"]);
  const [placedItems, setPlacedItems] = useState([]);
  const [showGame, setShowGame] = useState(false);
  const [message, setMessage] = useState("");

  function moveLeft() {
    if (bgIndex > 0) setBgIndex(bgIndex - 1);
  }
  function moveRight() {
    if (bgIndex < 3) setBgIndex(bgIndex + 1);
  }

  function captureScreen() {
    const appEl = document.querySelector(".app");
    if (!appEl) return;
    html2canvas(appEl).then(canvas => {
      const link = document.createElement("a");
      link.href = canvas.toDataURL("image/png");
      link.download = "my_ocean_capture.png";
      link.click();
    });
  }

  function handleTrash() {
    if (!window.confirm("모든 아이템을 제거할까요?")) return;
    setPlacedItems([]);
    alert("아이템이 모두 제거되었습니다.");
  }

  function handleGameReward(newItem) {
    setInventory(prev => [...prev, newItem]);
  }

  function handleExit() {
    if (window.confirm("정말 나가시겠습니까?")) {
      navigate("/");
    }
  }

  function sendMessageToFriend() {
    if (!message) {
      alert("메시지를 입력해주세요!");
      return;
    }
    alert(`메시지 전송: ${message}`);
    setMessage("");
    setModal("");
  }

  return (
    <div className="app">
      <div
        className="background-wrap"
        style={{ transform: `translateX(-${bgIndex * 25}%)` }}
      >
        {[...Array(4)].map((_, i) => (
          <div
            key={i}
            className="background"
            style={{ backgroundImage: "url('/background.png')" }}
          />
        ))}
      </div>

      <div
        className="fish-container"
        style={{ animationPlayState: fishPaused ? "paused" : "running" }}
      >
        {placedItems.map((item, i) => (
          <div
            key={i}
            style={{
              position: "absolute",
              top: `${20 + i * 10}%`,
              left: `${10 + i * 8}%`
            }}
          >
            🐚
          </div>
        ))}
      </div>

      <div className="left-menu">
        <button className="menu-btn" onClick={() => setModal("help")}>❓</button>
        <button className="menu-btn" onClick={() => setModal("message")}>💬</button>
        <button className="menu-btn" onClick={() => setShowGame(true)}>🎮</button>
        <button className="menu-btn" onClick={() => setModal("inventory")}>🎁</button>
        <button className="menu-btn" onClick={handleExit}>🚪</button>
      </div>

      <button className="arrow arrow-left" onClick={moveLeft}>⬅</button>
      <button className="arrow arrow-right" onClick={moveRight}>➡</button>

      <div className="bottom-btns">
        <button className="circle-btn" onClick={captureScreen}>📷</button>
        <button className="circle-btn" onClick={handleTrash}>🗑️</button>
      </div>

      {modal && (
        <div className="modal" onClick={() => setModal("")}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            {modal === "help" && (
              <>
                <h2>사용설명</h2>
                <ul style={{ textAlign: "left", paddingLeft: "20px" }}>
                  <li>아이템을 배치해 어항 꾸미기</li>
                  <li>미니게임으로 추가 아이템 획득</li>
                  <li>친구에게 메시지 남기기 가능</li>
                </ul>
              </>
            )}
            {modal === "message" && (
              <>
                <h2>메시지 남기기</h2>
                <textarea
                  value={message}
                  onChange={e => setMessage(e.target.value)}
                  style={{ width: "100%", height: "80px", marginTop: "10px" }}
                  placeholder="메시지를 입력하세요"
                />
                <button onClick={sendMessageToFriend} style={{ marginTop: "10px" }}>
                  보내기
                </button>
              </>
            )}
            {modal === "inventory" && (
              <>
                <h2>아이템 보관함</h2>
                {inventory.length === 0 ? (
                  <p>아이템 없음</p>
                ) : (
                  <ul style={{ listStyle: "none", padding: 0 }}>
                    {inventory.map((it, idx) => (
                      <li key={idx} style={{ margin: "5px 0" }}>
                        {it}
                        <button
                          onClick={() => setPlacedItems(prev => [...prev, it])}
                          style={{ marginLeft: "6px" }}
                        >
                          배치
                        </button>
                      </li>
                    ))}
                  </ul>
                )}
              </>
            )}
            <button onClick={() => setModal("")} style={{ marginTop: "10px" }}>
              닫기
            </button>
          </div>
        </div>
      )}

      {showGame && (
        <RpsGame
          onClose={() => setShowGame(false)}
          onReward={handleGameReward}
        />
      )}

      <audio id="captureSound" src="capture.mp3"></audio>
    </div>
  );
}
