import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Archive({ token }) {
  const [inventory, setInventory] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (token) {
      fetch('/api/items', {
        headers: { 'Authorization': `Bearer ${token}` }
      })
        .then(res => res.json())
        .then(data => {
          if (data.inventory) {
            setInventory(data.inventory);
          }
        })
        .catch(err => console.error('Failed to fetch inventory:', err));
    }
  }, [token]);

  const handlePlace = (itemId) => {
    fetch(`/api/items/${itemId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify({ placed: true })
    })
      .then(res => res.json())
      .then(data => {
        if (data && data._id) {
          alert('ÏïÑÏù¥ÌÖúÏùÑ Ïñ¥Ìï≠Ïóê Î∞∞ÏπòÌñàÏäµÎãàÎã§!');
          navigate('/aquarium');
        } else {
          alert('ÏïÑÏù¥ÌÖú Î∞∞Ïπò Ïã§Ìå®');
        }
      })
      .catch(err => {
        console.error('Place item error:', err);
        alert('ÏïÑÏù¥ÌÖú Î∞∞Ïπò Ïã§Ìå®');
      });
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Î≥¥Í¥ÄÌï®</h2>
      {inventory.length === 0 ? (
        <p>Î≥¥Í¥ÄÌï®Ïù¥ ÎπÑÏóàÏäµÎãàÎã§.</p>
      ) : (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {inventory.map(item => (
            <li key={item._id} style={{ marginBottom: '10px' }}>
              <span style={{ marginRight: '10px' }}>üêö {item.name}</span>
              <button onClick={() => handlePlace(item._id)}>Î∞∞Ïπò</button>
            </li>
          ))}
        </ul>
      )}
      <button onClick={() => navigate('/aquarium')}>‚Üê ÎèåÏïÑÍ∞ÄÍ∏∞</button>
    </div>
  );
}
