import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import scissorsImg from '../../public/assets/scissors.png';
import rockImg from '../../public/assets/rock.png';
import paperImg from '../../public/assets/paper.png';

const choices = [
  { name: '가위', img: scissorsImg },
  { name: '바위', img: rockImg },
  { name: '보', img: paperImg },
];

function getResult(player, computer) {
  if (player === computer) return '무승부';
  if (
    (player === '가위' && computer === '보') ||
    (player === '바위' && computer === '가위') ||
    (player === '보' && computer === '바위')
  ) return '승리';
  return '패배';
}

export default function RpsGame({ token }) {
  const [round, setRound] = useState(1);
  const [wins, setWins] = useState(0);
  const [gameOver, setGameOver] = useState(false);
  const [result, setResult] = useState('');
  const [playerChoice, setPlayerChoice] = useState(null);
  const [enemyChoice, setEnemyChoice] = useState(null);
  const [showResult, setShowResult] = useState(false);
  const navigate = useNavigate();

  const handleChoice = (choice) => {
    const random = choices[Math.floor(Math.random() * 3)];
    const res = getResult(choice.name, random.name);

    setPlayerChoice(choice);
    setEnemyChoice(random);
    setResult(res);
    setShowResult(true);

    if (res === '승리') setWins(w => w + 1);
    else if (res === '패배') setGameOver(true);
  };

  const nextRound = () => {
    if (round >= 7) {
      handleReward();
      return;
    }
    setRound(r => r + 1);
    setPlayerChoice(null);
    setEnemyChoice(null);
    setResult('');
    setShowResult(false);
  };

  const handleReward = () => {
    let grade = 'C';
    if (wins >= 4 && wins < 7) grade = 'B';
    if (wins === 7) grade = 'A';

    fetch('/api/items', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify({ name: `${grade}등급 상자` })
    })
      .then(() => {
        alert(`${grade}등급 상자를 획득했습니다!`);
        navigate('/aquarium');
      })
      .catch(err => console.error('Reward error:', err));
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gradient-to-br from-blue-100 to-purple-100 p-4">
      {!gameOver && !showResult && (
        <>
          <h2 className="text-2xl font-bold mb-4">{round}번째 판</h2>
          <p className="mb-6">가위, 바위, 보 중 하나를 선택하세요!</p>
          <div className="flex gap-6">
            {choices.map(choice => (
              <motion.button
                key={choice.name}
                whileTap={{ scale: 0.8 }}
                onClick={() => handleChoice(choice)}
                className="p-2 bg-white rounded-xl shadow hover:scale-105 transition"
              >
                <img src={choice.img} alt={choice.name} className="w-24 h-24" />
                <p className="mt-2 font-medium">{choice.name}</p>
              </motion.button>
            ))}
          </div>
        </>
      )}

      {showResult && !gameOver && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          className="mt-10 text-center"
        >
          <h3 className="text-xl mb-2">
            당신: {playerChoice?.name} / 상대: {enemyChoice?.name}
          </h3>
          <h2 className="text-3xl font-bold text-purple-700 mb-4">{result}</h2>
          <div className="flex gap-4 justify-center">
            <button onClick={nextRound} className="bg-blue-500 text-white px-4 py-2 rounded">계속하기</button>
            <button onClick={() => setGameOver(true)} className="bg-gray-400 text-white px-4 py-2 rounded">포기하기</button>
          </div>
        </motion.div>
      )}

      {gameOver && (
        <motion.div
          initial={{ scale: 0.8, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          className="mt-10 text-center"
        >
          <h2 className="text-2xl font-bold mb-2">게임 종료!</h2>
          <p className="mb-4">총 승리 수: {wins}</p>
          <button onClick={handleReward} className="bg-green-500 text-white px-4 py-2 rounded">보상 받기</button>
        </motion.div>
      )}
    </div>
  );
}
