import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate, useNavigate } from 'react-router-dom';
import Aquarium from './components/Aquarium';
import Login from './components/Login';
import Signup from './components/Signup';
import MiniGame from './components/MiniGame';
import Archive from './components/Archive';
import OceanView from './pages/OceanView';
import DecorateView from './pages/DecorateView';
import FriendOcean from './pages/FriendOcean';

import KakaoCallback from './components/Auth/KakaoCallback';
import './App.css'; // App.css가 있다면 스타일 반영

export default function App() {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const navigate = useNavigate();

  useEffect(() => {
    if (token && !user) {
      fetch('/api/auth/me', {
        headers: { 'Authorization': `Bearer ${token}` }
      })
        .then(res => res.ok ? res.json() : Promise.reject(res.status))
        .then(data => setUser(data))
        .catch(err => {
          console.error('Failed to fetch user info:', err);
          localStorage.removeItem('token');
          setToken('');
          navigate('/login');
        });
    }
  }, [token]);

  const handleLoginSuccess = (newToken, newUser) => {
    localStorage.setItem('token', newToken);
    setToken(newToken);
    setUser(newUser);
  };

  const RequireAuth = ({ children }) => {
    return token ? children : <Navigate to="/login" replace />;
  };

  return (
    <div className="app"> {/* ✅ 핵심 수정: 전체 앱을 감싸는 div */}
      <Routes>
        <Route path="/" element={token ? <Navigate to="/aquarium" replace /> : <Navigate to="/login" replace />} />
        <Route path="/login" element={<Login onLogin={handleLoginSuccess} />} />
        <Route path="/signup" element={<Signup onSignup={handleLoginSuccess} />} />
        <Route path="/friend" element={<FriendOcean />} />
        <Route path="/aquarium" element={<RequireAuth><Aquarium user={user} token={token} /></RequireAuth>} />
        <Route path="/minigame" element={<RequireAuth><MiniGame token={token} /></RequireAuth>} />
        <Route path="/archive" element={<RequireAuth><Archive token={token} /></RequireAuth>} />
        <Route path="/ocean/:username" element={<OceanView />} />
        <Route path="/decorate/:username" element={<DecorateView />} />
        <Route path="/fishposts" element={<FishPosts />} />
        <Route path="/oauth/kakao/callback" element={<KakaoCallback />} />
      </Routes>
    </div>
  );
}
