// src/components/Login.jsx
import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Login.css";

export default function Login() {
  const navigate = useNavigate();

  // isMemberLogin: false → 첫 화면(카카오/회원로그인 버튼)
  //               true  → 이메일/비번 입력창
  const [isMemberLogin, setIsMemberLogin] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleKakaoLogin = () => {
    alert("카카오톡 로그인 로직 (추가 구현 필요)");
    // 로그인 성공 후
    // navigate("/my");
  };

  const handleMemberLogin = () => {
    alert(`회원 로그인: 이메일=${email}, 비밀번호=${password}`);
    // fetch("/api/auth/login", { ... });
    // navigate("/my");
  };

  return (
    <div className="login-page">
      <div className="login-card">
        {!isMemberLogin ? (
          <>
            <button className="login-btn" onClick={handleKakaoLogin}>
              카카오톡으로 로그인
            </button>
            <button
              className="login-btn"
              onClick={() => setIsMemberLogin(true)}
            >
              회원으로 로그인
            </button>
            <div className="signup-link">
              회원이 아니신가요?{" "}
              <Link to="/signup" style={{ textDecoration: "underline" }}>
                회원가입
              </Link>
            </div>
          </>
        ) : (
          <>
            <h2 className="login-title">로그인</h2>
            <input
              type="text"
              placeholder="아이디를 입력하세요..."
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="password"
              placeholder="비밀번호를 입력하세요..."
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button className="btn-confirm" onClick={handleMemberLogin}>
              확인
            </button>
            <button className="btn-back" onClick={() => setIsMemberLogin(false)}>
              뒤로
            </button>
          </>
        )}
      </div>
    </div>
  );
}
