// src/pages/BitFishFlow.jsx
import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import html2canvas from "html2canvas";
import "../App.css";

/*
  1) BitFishFlow Ïª¥Ìè¨ÎÑåÌä∏:
   - step stateÎ°ú ÌîÑÎ†àÏûÑ(18~22) Ïù¥Îèô
   - step=6Ïù¥ ÎêòÎ©¥ DecorateViewÎ°ú ÎÑòÏñ¥Í∞ÄÏÑú Ïã§Ï†ú Ïñ¥Ìï≠ Íæ∏ÎØ∏Í∏∞
*/
export default function BitFishFlow() {
  const [step, setStep] = useState(1);

  // Îã§Ïùå/Ïù¥Ï†Ñ Îã®Í≥Ñ Ïù¥Îèô
  const nextStep = () => setStep((prev) => (prev < 6 ? prev + 1 : 6));
  const prevStep = () => setStep((prev) => (prev > 1 ? prev - 1 : 1));

  return (
    <div className="bitfish-container">
      <div className="bitfish-content">
        {step === 1 && <Frame18 nextStep={nextStep} />}
        {step === 2 && <Frame19 nextStep={nextStep} prevStep={prevStep} />}
        {step === 3 && <Frame20 nextStep={nextStep} prevStep={prevStep} />}
        {step === 4 && <Frame21 nextStep={nextStep} prevStep={prevStep} />}
        {step === 5 && <Frame22 nextStep={nextStep} prevStep={prevStep} />}

        {/* step=6Ïù¥Î©¥ DecorateViewÎ°ú ÏßÑÏûÖ */}
        {step === 6 && <DecorateView />}
      </div>
    </div>
  );
}

/* ===================
   2) Îã®Í≥ÑÎ≥Ñ Ïª¥Ìè¨ÎÑåÌä∏
   (Frame 18 ~ 22)
=================== */
// Ïòà) Frame18
function Frame18({ nextStep }) {
  return (
    <div className="bitfish-card">
      <h2>ÏπúÍµ¨Í∞Ä Îì§Ïñ¥ÏôîÏñ¥Ïöî!</h2>
      <p>ÎØ∏ÎãàÍ≤åÏûÑÏùÑ Ìï¥Î≥ºÍπåÏöî?</p>
      <button onClick={nextStep} className="bitfish-btn">
        ÎØ∏ÎãàÍ≤åÏûÑ
      </button>
    </div>
  );
}

function Frame19({ nextStep, prevStep }) {
  return (
    <div className="bitfish-card">
      <h2>Îï°Îï° ÎØ∏ÎãàÍ≤åÏûÑ</h2>
      <p>Ïû¨ÎØ∏ÏûàÎäî ÎØ∏ÎãàÍ≤åÏûÑÏùÑ Ï§ÄÎπÑÌñàÏñ¥Ïöî!</p>
      <div className="bitfish-btns-row">
        <button onClick={prevStep} className="bitfish-btn">
          Ïù¥Ï†Ñ
        </button>
        <button onClick={nextStep} className="bitfish-btn">
          Îã§Ïùå
        </button>
      </div>
    </div>
  );
}

function Frame20({ nextStep, prevStep }) {
  return (
    <div className="bitfish-card">
      <h2>ÎØ∏ÎãàÍ≤åÏûÑ ÏãúÏûë</h2>
      <button className="bitfish-btn" onClick={() => alert("Í≤åÏûÑ ÏãúÏûë!")}>
        Í≤åÏûÑ ÏãúÏûë
      </button>
      <button className="bitfish-btn" onClick={() => alert("Í≤åÏûÑ ÌûåÌä∏!")}>
        ÎØ∏ÎãàÍ≤åÏûÑ ÌûåÌä∏
      </button>
      <button className="bitfish-btn" onClick={nextStep}>
        ÎØ∏ÎãàÍ≤åÏûÑ ÎÇòÍ∞ÄÍ∏∞
      </button>
      <button className="bitfish-btn" onClick={prevStep} style={{ marginTop: "10px" }}>
        Ïù¥Ï†Ñ
      </button>
    </div>
  );
}

function Frame21({ nextStep, prevStep }) {
  return (
    <div className="bitfish-card">
      <h2>ÏïÑÏù¥ÌÖú ÌöçÎìù!</h2>
      <div className="bitfish-item-box">
        <img
          src="/assets/treasure.png"
          alt="Î≥¥Î¨º ÏÉÅÏûê"
          style={{ width: "150px", marginBottom: "10px" }}
        />
        <p>ÏßúÏûî~! Î≥¥Î¨º ÏÉÅÏûêÎ•º Î∞úÍ≤¨ÌñàÏñ¥Ïöî.</p>
      </div>
      <div className="bitfish-btns-row">
        <button className="bitfish-btn" onClick={prevStep}>
          Ïù¥Ï†Ñ
        </button>
        <button className="bitfish-btn" onClick={nextStep}>
          Îã§Ïùå
        </button>
      </div>
    </div>
  );
}

function Frame22({ nextStep, prevStep }) {
  return (
    <div className="bitfish-card">
      <h2>ÏÉàÎ°úÏö¥ Î¨ºÍ≥†Í∏∞!</h2>
      <div className="bitfish-item-box">
        <img
          src="/assets/clownfish.png"
          alt="ÌÅ¥ÎùºÏö¥ÌîºÏâ¨"
          style={{ width: "150px", marginBottom: "10px" }}
        />
        <p>Î©ãÏßÑ Î¨ºÍ≥†Í∏∞Î•º ÌöçÎìùÌñàÏñ¥Ïöî!</p>
      </div>
      <div className="bitfish-btns-row">
        <button className="bitfish-btn" onClick={prevStep}>
          Ïù¥Ï†Ñ
        </button>
        {/* ÎßàÏßÄÎßâ Îã®Í≥ÑÏóêÏÑú Ïñ¥Ìï≠ Íæ∏ÎØ∏Í∏∞ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô */}
        <button className="bitfish-btn" onClick={nextStep}>
          Ïñ¥Ìï≠ÏúºÎ°ú Ïù¥Îèô
        </button>
      </div>
    </div>
  );
}

/* ===================
   3) DecorateView
   (Ïñ¥Ìï≠ Íæ∏ÎØ∏Í∏∞)
=================== */
function DecorateView() {
  // username Îì±ÏùÄ useParamsÎ°ú Î∞õÍ±∞ÎÇò, step Î°úÏßÅ ÏÉÅ ÎÑòÏñ¥Ïò® propsÎ°ú Î∞õÏùÑ ÏàòÎèÑ ÏûàÏùå
  // Ïó¨Í∏∞ÏÑúÎäî ÏòàÏãúÎ°ú username Í≥†Ï†ï
  const username = "younk";
  const navigate = useNavigate();

  const [bgIndex, setBgIndex] = useState(0);
  const [musicPlaying, setMusicPlaying] = useState(false);
  const [fishPaused, setFishPaused] = useState(false);
  const [modal, setModal] = useState("");
  const [inventory, setInventory] = useState([]);
  const [placedItems, setPlacedItems] = useState([]);

  useEffect(() => {
    fetch(`/api/items/${username}`)
      .then((res) => res.json())
      .then((data) => {
        if (data && Array.isArray(data.inventory)) {
          setInventory(data.inventory);
        }
      })
      .catch((err) => console.error("ÏïÑÏù¥ÌÖú Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®", err));
  }, [username]);

  const moveLeft = () => {
    if (bgIndex > 0) setBgIndex(bgIndex - 1);
  };
  const moveRight = () => {
    if (bgIndex < 3) setBgIndex(bgIndex + 1);
  };

  const captureScreen = () => {
    const appElement = document.querySelector(".app");
    if (!appElement) return;
    html2canvas(appElement).then((canvas) => {
      const link = document.createElement("a");
      link.href = canvas.toDataURL("image/png");
      link.download = "my_ocean_capture.png";
      link.click();
    });
    const sound = document.getElementById("captureSound");
    if (sound) sound.play();
  };

  const copyUrl = () => {
    navigator.clipboard.writeText(window.location.href).then(() => alert("URLÏù¥ Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§!"));
  };

  // ÏïÑÏù¥ÌÖú Î∞∞Ïπò
  const handlePlace = (itemId) => {
    setPlacedItems((prev) => [...prev, itemId]);
  };

  // Î∞∞Ïπò ÏïÑÏù¥ÌÖú ÏÑúÎ≤Ñ Ï†ÄÏû•
  const handleSubmit = () => {
    fetch(`/api/ocean/${username}`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ items: placedItems }),
    })
      .then((res) => {
        if (res.ok) {
          alert("Ïñ¥Ìï≠ÏùÑ Íæ∏Î©∞Ï§¨Ïñ¥Ïöî!");
          navigate(`/ocean/${username}`);
        } else {
          alert("Ï†ÄÏû• Ïã§Ìå® „Ö†„Ö†");
        }
      });
  };

  return (
    <div className="app">
      {/* Î∞∞Í≤Ω Ïä¨ÎùºÏù¥Îìú */}
      <div className="background-wrap" style={{ transform: `translateX(-${bgIndex * 25}%)` }}>
        {[...Array(4)].map((_, i) => (
          <div key={i} className="background" style={{ backgroundImage: "url('/background.png')" }}></div>
        ))}
      </div>

      {/* Ïñ¥Ìï≠(Î¨ºÍ≥†Í∏∞, Î∞∞Ïπò ÏïÑÏù¥ÌÖú Îì±) */}
      <div className="fish-container" style={{ animationPlayState: fishPaused ? "paused" : "running" }}>
        {placedItems.map((itemId, i) => (
          <div
            key={i}
            className="placed-item"
            style={{
              position: "absolute",
              top: `${20 + i * 10}%`,
              left: `${20 + i * 10}%`,
            }}
          >
            üêö
          </div>
        ))}
      </div>

      {/* Ï¢åÏ∏° Î©îÎâ¥ */}
      <div className="left-menu">
        <button className="menu-btn" onClick={() => setModal("help")}>üìú</button>
        <button className="menu-btn" onClick={() => setModal("setting")}>‚öôÔ∏è</button>
        <button className="menu-btn" onClick={() => setModal("copy")}>üîó</button>
        <button className="menu-btn" onClick={() => navigate("/")}>‚¨ÖÔ∏è</button>
      </div>

      {/* Î∞∞Í≤Ω Ïù¥Îèô */}
      <button className="arrow arrow-left" onClick={moveLeft}>‚¨Ö</button>
      <button className="arrow arrow-right" onClick={moveRight}>‚û°</button>

      {/* Ï∫°Ï≤ò Î≤ÑÌäº */}
      <div className="bottom-btns">
        <button className="circle-btn" onClick={captureScreen}>üì∑</button>
      </div>

      {/* Î™®Îã¨ */}
      {modal && (
        <div className="modal" onClick={() => setModal("")}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            {modal === "help" && (
              <>
                <div className="cute-icon">üòä</div>
                <h2>ÎèÑÏõÄÎßê</h2>
                <ul style={{ textAlign: "left", paddingLeft: "20px" }}>
                  <li>ÏïÑÏù¥ÌÖúÏùÑ ÏÑ†ÌÉùÌï¥ÏÑú ÏπúÍµ¨ Ïñ¥Ìï≠Ïóê Î∞∞ÏπòÌï† Ïàò ÏûàÏñ¥Ïöî.</li>
                  <li>Í≤åÏûÑÏùÑ ÌÜµÌï¥ ÏñªÏùÄ ÏïÑÏù¥ÌÖúÏù¥ Ïó¨Í∏∞Ïóê Î≥¥Ïó¨Ïöî.</li>
                </ul>
              </>
            )}
            {modal === "setting" && (
              <>
                <div className="cute-icon">üéõÔ∏è</div>
                <h2>ÏÑ§Ï†ï</h2>
                <div className="setting-options">
                  <div
                    className="setting-option"
                    onClick={() => {
                      setMusicPlaying(!musicPlaying);
                      alert(musicPlaying ? "ÏùåÏïÖÏù¥ Ï†ïÏßÄÎê©ÎãàÎã§." : "ÏùåÏïÖÏù¥ Ïû¨ÏÉùÎê©ÎãàÎã§.");
                    }}
                  >
                    üîá
                  </div>
                  <div
                    className="setting-option"
                    onClick={() => {
                      setFishPaused(!fishPaused);
                      alert(fishPaused ? "Î¨ºÍ≥†Í∏∞ Ïï†ÎãàÎ©îÏù¥ÏÖòÏù¥ Ï†ïÏßÄÎêòÏóàÏäµÎãàÎã§." : "Î¨ºÍ≥†Í∏∞ Ïï†ÎãàÎ©îÏù¥ÏÖòÏù¥ Ïû¨ÏÉùÎê©ÎãàÎã§.");
                    }}
                  >
                    ‚è∏Ô∏è
                  </div>
                </div>
              </>
            )}
            {modal === "copy" && (
              <>
                <div className="cute-icon">üîó</div>
                <h2>URL Î≥µÏÇ¨</h2>
                <div className="url-box">
                  <span>{window.location.href}</span>
                  <button onClick={copyUrl}>Î≥µÏÇ¨</button>
                </div>
              </>
            )}
            <button onClick={() => setModal("")}>Îã´Í∏∞</button>
          </div>
        </div>
      )}

      {/* (Ï∂îÍ∞Ä) Í≤åÏûÑÌï¥ÏÑú ÏïÑÏù¥ÌÖú ÏñªÍ∏∞ Î≤ÑÌäº */}
      <div style={{ position: "absolute", top: "10px", right: "10px" }}>
        <button
          className="menu-btn"
          style={{ fontSize: "0.9rem", padding: "8px 12px", background: "#00bcd4" }}
          onClick={() => alert("ÎØ∏ÎãàÍ≤åÏûÑÏúºÎ°ú Ïù¥Îèô! (Ïó∞Í≤∞ ÌïÑÏöî)")}
        >
          üéÆ Í≤åÏûÑÌï¥ÏÑú ÏïÑÏù¥ÌÖú ÏñªÍ∏∞
        </button>
      </div>

      {/* ÏïÑÏù¥ÌÖú Î™©Î°ù */}
      <div
        style={{
          position: "absolute",
          bottom: "80px",
          left: "20px",
          background: "#fff",
          padding: "10px",
          borderRadius: "10px",
        }}
      >
        <h3 style={{ margin: 0 }}>üéÅ ÏπúÍµ¨Í∞Ä Í∞ÄÏßÑ ÏïÑÏù¥ÌÖú</h3>
        {inventory.length === 0 ? (
          <p>ÏïÑÏù¥ÌÖú ÏóÜÏùå</p>
        ) : (
          <ul style={{ listStyle: "none", padding: 0 }}>
            {inventory.map((item) => (
              <li key={item._id} style={{ marginTop: "5px" }}>
                üêö {item.name}
                <button onClick={() => handlePlace(item._id)} style={{ marginLeft: "10px" }}>
                  Î∞∞Ïπò
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* Ï†ÄÏû• Î≤ÑÌäº */}
      <button
        style={{ position: "absolute", bottom: "20px", right: "20px" }}
        onClick={handleSubmit}
      >
        Ï†ÄÏû•ÌïòÍ≥† ÏπúÍµ¨ÏóêÍ≤å Î≥¥ÎÇ¥Í∏∞ ‚ú®
      </button>

      <audio id="captureSound" src="capture.mp3"></audio>
    </div>
  );
}
