import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

export default function Login({ onLogin }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('/api/auth/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email, password })
    })
      .then(res => res.json())
      .then(data => {
        if (data.token) {
          onLogin(data.token, data.user);
          navigate('/aquarium');
        } else {
          alert('로그인 실패: ' + (data.message || '알 수 없는 오류'));
        }
      })
      .catch(err => {
        console.error('Login error:', err);
        alert('로그인 실패: 서버 오류');
      });
  };

  const handleKakaoLogin = () => {
    // Kakao OAuth simulation
    fetch('/api/auth/kakao/callback')
      .then(res => res.json())
      .then(data => {
        if (data.token) {
          onLogin(data.token, data.user);
          navigate('/aquarium');
        } else {
          alert('카카오 로그인 실패');
        }
      })
      .catch(err => {
        console.error('Kakao login error:', err);
        alert('카카오 로그인 실패');
      });
  };

  return (
    <div className="login-container" style={{ textAlign: 'center', marginTop: '100px' }}>
      <h2>로그인</h2>
      <form onSubmit={handleSubmit} style={{ display: 'inline-block', textAlign: 'left' }}>
        <div>
          <label htmlFor="email">이메일: </label>
          <input
            type="email" id="email" value={email}
            onChange={e => setEmail(e.target.value)} required
          />
        </div>
        <div>
          <label htmlFor="password">비밀번호: </label>
          <input
            type="password" id="password" value={password}
            onChange={e => setPassword(e.target.value)} required
          />
        </div>
        <button type="submit">로그인</button>
      </form>
      <div style={{ marginTop: '10px' }}>
        <button onClick={handleKakaoLogin} style={{ background: '#F7E600', border: 'none', padding: '8px 12px', cursor: 'pointer' }}>
          카카오로 로그인
        </button>
      </div>
      <div style={{ marginTop: '10px' }}>
        계정이 없으신가요? <Link to="/signup">회원가입</Link>
      </div>
    </div>
  );
}
